import Link from "next/link";
import fetch from "isomorphic-unfetch";
import "../assets/css/featurednewscomponents.scss";
import Navbar from "./navbar";
import BackgroundVideo from "./backgroundvideo";

const FeaturedNews = ({ featuredNews }) => {
  console.log({ featuredNews: featuredNews });
  const { API_URL } = process.env;
  return (
    <div>
      {featuredNews.map((featuredNew) => (
        <div>
          <img
            id="fea-card-img"
            src={API_URL + featuredNew.featured_video.url}
          />
          <p id="fealine">{featuredNew.featuredDes}</p>
        </div>
      ))}
    </div>
  );
};

export async function getServerSideProps() {
  const { API_URL } = process.env;

  const res = await fetch(`${API_URL}/featured-news`);
  const data = await res.json();

  return {
    props: {
      featuredNews: data,
    },
  };
}

export default FeaturedNews;
