import Link from "next/link";
import fetch from "isomorphic-unfetch";
import "../assets/css/featurednewscomponents.scss";

const FeaturedNews = ({ featuredNews }) => {
  console.log({ featuredNews: featuredNews });
  const { API_URL } = process.env;
  return (
    <div>
      {featuredNews.map((featuredNew) => (
        <h1>{featuredNew.featuredDes}</h1>
      ))}
    </div>
  );
};

export async function getServerSideProps() {
  const { API_URL } = process.env;

  const res = await fetch(`${API_URL}/featured-news`);
  const data = await res.json();

  return {
    props: {
      featuredNews: data,
    },
  };
}

export default FeaturedNews;
