import Header from "../components/Header";
import { motion } from "framer-motion";
import getConfig from "next/config";
import fetch from "isomorphic-unfetch";

function featured({ news }) {
  console.log(news);
  return (
    <motion.div
      exit={{ opacity: 0 }}
      inital={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <Header />
      <div style={{ width: "50%", margin: "0 auto", paddingTop: "10vh" }}>
        <h2 style={{ color: "white" }}>{news.title}</h2>
        <p
          style={{ color: "white" }}
          dangerouslySetInnerHTML={{ __html: news.featuredDes }}
        ></p>
      </div>
    </motion.div>
  );
}

const { publicRuntimeConfig } = getConfig();

export async function getServerSideProps(context) {
  const { featured } = context.query;
  const { API_URL } = process.env;
  const res = await fetch(`${API_URL}/featured-news/${featured}`);
  const data = await res.json();
  console.log(featured);
  return {
    props: {
      news: data,
    },
  };
}

export default featured;
